CREATE SEQUENCE book_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1;
GRANT USAGE, SELECT ON SEQUENCE book_id_seq TO nlbr;
CREATE SEQUENCE review_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE  CACHE 1;
GRANT USAGE, SELECT ON SEQUENCE review_id_seq TO nlbr;
CREATE TABLE IF NOT EXISTS public.book
(
    id integer NOT NULL DEFAULT nextval('book_id_seq'::regclass),
    title character varying(255) COLLATE pg_catalog."default" NOT NULL,
    author character varying(255) COLLATE pg_catalog."default" NOT NULL,
    cover text COLLATE pg_catalog."default",
    owner character varying(255) COLLATE pg_catalog."default" NOT NULL,
    CONSTRAINT book_pkey PRIMARY KEY (id)
);
ALTER TABLE IF EXISTS public.book OWNER to nlbr;
CREATE TABLE IF NOT EXISTS public.review
(
    id integer NOT NULL DEFAULT nextval('review_id_seq'::regclass),
    review character varying(2000) COLLATE pg_catalog."default" NOT NULL,
    owner character varying(255) COLLATE pg_catalog."default" NOT NULL,
    name character varying(255) COLLATE pg_catalog."default" NOT NULL,
    book_id integer NOT NULL,
    CONSTRAINT review_pkey PRIMARY KEY (id),
    CONSTRAINT book_id_fk FOREIGN KEY (book_id)
        REFERENCES public.book (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
);
ALTER TABLE IF EXISTS public.review OWNER to nlbr;

DO $$ 
DECLARE
  max_book_id integer;
BEGIN
  SELECT MAX(id) INTO max_book_id FROM public.book;
  IF max_book_id IS NOT NULL THEN
    PERFORM setval('book_id_seq', max_book_id + 1);
  END IF;
END $$;
DO $$ 
DECLARE
  max_review_id integer;
BEGIN
  SELECT MAX(id) INTO max_review_id FROM public.review;
  IF max_review_id IS NOT NULL THEN
    PERFORM setval('review_id_seq', max_review_id + 1);
  END IF;
END $$;
