INSERT INTO  public.review (review, owner, name, book_id) VALUES ('Not a happy read - what did you expect from the title? - but a magnificent book.  A telepath coming to terms with the fading of his powers.  If this were not SF, it would be regarded as a classic.', 'rattan.kevin.k@fakegmail.com', 'Kevin Rattan', 37);
INSERT INTO  public.review (review, owner, name, book_id) VALUES ('A vision of a world like ancient Egypt, but not...  Science Fiction, but with a strong fantasy feel.  Wonderful.', 'rattan.kevin.k@fakegmail.com', 'Kevin Rattan', 38);
INSERT INTO  public.review (review, owner, name, book_id) VALUES ('I find it depressing that this is not as much a part of everyone''s childhood as, say, Winnie the Pooh.  If you know a child who has not read this, buy them a copy.  Something is badly wrong with a world where this is out of print.', 'rattan.kevin.k@fakegmail.com', 'Kevin Rattan', 39);
